def createPeople(Descriptions):
    people=[]
    i=0
    while i<len(Descriptions):
        if Descriptions[i].startswith("CHILD"):
            j=1
            lst=Descriptions[i].split(" ")
            while j<len(lst):
                if {"name": lst[j]} not in people:
                    people.append({"name": lst[j]})
                    j+=1
                else:
                    j+=1
            i+=1
        elif Descriptions[i].startswith("MARRIED"):
            lst=Descriptions[i].split(" ")
            if {"name": lst[1]} not in people:
                people.append({"name": lst[1]})
            if {"name": lst[2]} not in people:
                people.append({"name": lst[2]})
            i+=1
        elif Descriptions[i].startswith("DEPARTED"):
            lst=Descriptions[i].split(" ")
            if {"name": lst[1]} not in people:
                people.append({"name": lst[1]})
            i+=1
        elif Descriptions[i].startswith("DECEASED"):
            lst=Descriptions[i].split(" ")
            if {"name": lst[1]} not in people:
                people.append({"name": lst[1]})
            i+=1
    return people

def read(Descriptions,people):
    for person in people:
        if "spouse" not in person:
            person["spouse"]=""
        if "alive" not in person:
            person["alive"]="yes"
        if "parents" not in person:
            person["parents"]=[]
        if "children" not in person:
            person["children"]=[]
        if "grandchildren" not in person:
            person["grandchildren"]=[]
        if "grandparents" not in person:
            person["grandparents"]=[]
    for element in Descriptions:
        if element.startswith("CHILD"):
            lst=element.split(" ")
            j=3
            while j<len(lst):
                for person in people:
                    if person["name"]==lst[1]:
                        if "children" not in person:
                            person["children"]=[]
                        person["children"].append(lst[j])
                    if person["name"]==lst[2]:
                        if "children" not in person:
                            person["children"]=[]
                        person["children"].append(lst[j])
                    if person["name"]==lst[j]:
                        person["parents"]=lst[1],lst[2]
                j+=1
        elif element.startswith("MARRIED"):
            lst=element.split(" ")
            for person in people:
                if person["name"]==lst[1]:
                    person["spouse"]=lst[2]
                if person["name"]==lst[2]:
                    person["spouse"]=lst[1]
        elif element.startswith("DEPARTED"):
            lst=element.split(" ")
            for person in people:
                if person["name"]==lst[1]:
                    person["alive"]="no"
        elif element.startswith("DECEASED"):
            lst=element.split(" ")
            for person in people:
                if person["name"]==lst[1]:
                    person["alive"]="no"
    for person1 in people:
        for person2 in people:
            if person1["children"]!=[] and (person2["name"] in person1["children"]):
                for person3 in people:
                    if person2["children"]!=[] and (person3["name"] in person2["children"]):
                        person1["grandchildren"].append(person3["name"])
    for person4 in people:
        for person5 in people:
            if person4["parents"]!=[] and (person5["name"] in person4["parents"]):
                for person6 in people:
                    if person5["parents"]!=[] and (person6["name"] in person5["parents"]):
                        person4["grandparents"].append(person6["name"])
    
    return people

